mapper.select:
SELECT A.orderNo as orderId, A.addTimestamp as orderDateDB, A.handleFlg as orderStatus, A.orderAmount as orderAmount, A.deliveryMethod, A.deliveryCost, B.receiver as consignee from t_cons_order A left join t_address_info B on B.id = A.addressId where A.handleFlg <> '9' and A.customerNo = #{customerNo,jdbcType=VARCHAR}
and A.orderNo = #{orderNo,jdbcType=VARCHAR}
order by addTimestamp desc

		SELECT count(1)
		FROM t_cons_order 
		WHERE handleFlg  <>  '9'
		and customerNo = #{customerNo,jdbcType=VARCHAR}

        	and orderNo = #{orderNo,jdbcType=VARCHAR}

SELECT A.customerNo as customerNo, A.orderNo as orderNo, DATE_FORMAT(A.addTimestamp,'%Y/%m/%d') as orderTime, A.handleFlg as orderStatusView, A.orderAmount as orderAmount, A.deliveryMethod as deliveryMethodView, A.paymentMethod as paymentMethod, A.deliveryCost as deliveryCost, B.receiver as address from t_cons_order A left join t_address_info B on B.id = A.addressId where 1=1
and A.customerNo like '%' || #{customerNo,jdbcType=VARCHAR} || '%'
and A.handleFlg = #{orderStatus,jdbcType=VARCHAR}
and A.paymentMethod = #{payment,jdbcType=VARCHAR}
and A.deliveryMethod = #{deliveryMethod,jdbcType=VARCHAR}
and (DATE_FORMAT(A.orderTimestamp,'%Y/%m/%d')) >= #{dataFrom,jdbcType=VARCHAR}
and (DATE_FORMAT(A.orderTimestamp,'%Y/%m/%d')) <= #{dataTo,jdbcType=VARCHAR}
order by A.addTimestamp desc

		SELECT count(1)
		FROM t_cons_order A
		where 1=1

			and A.customerNo like '%' || #{customerNo,jdbcType=VARCHAR} || '%'


        	and A.handleFlg = #{orderStatus,jdbcType=VARCHAR}


        	and A.paymentMethod = #{payment,jdbcType=VARCHAR}


        	and A.deliveryMethod = #{deliveryMethod,jdbcType=VARCHAR}


        	and (DATE_FORMAT(A.orderTimestamp,'%Y/%m/%d')) >= #{dataFrom,jdbcType=VARCHAR}


        	and (DATE_FORMAT(A.orderTimestamp,'%Y/%m/%d')) <= #{dataTo,jdbcType=VARCHAR}
