mapper.select:
select

from t_goods_group
where 1 = 1

	and goodsId = #{goodsid,jdbcType=VARCHAR}


	and groupNo = #{groupno,jdbcType=VARCHAR}


	and openFlg = #{openflg,jdbcType=CHAR}

select A.groupNo as groupId, A.goodsId as goodsId, A.groupPrice as goodsPrice, A.groupMaxQuantity as goodsMax, A.groupCurrentQuantity as goodsCurr, A.openFlg as isOpen, DATE_FORMAT(A.validPeriodStart,'%Y/%m/%d') as validDateFrom, DATE_FORMAT(A.validPeriodEnd,'%Y/%m/%d') as validDateTo, B.goodsName as goodsName, C.priceValue as cost from t_goods_group A inner join t_goods B on A.goodsId = B.goodsId inner join t_goods_price C on A.goodsId = C.goodsId WHERE A.openFlg <> '9'
and B.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')
and A.goodsId = #{goodsId,jdbcType=VARCHAR}
and A.openFlg = #{isOpenFlag,jdbcType=VARCHAR}
and (DATE_FORMAT(A.validPeriodStart,'%Y/%m/%d')) >= #{dateFrom,jdbcType=VARCHAR}
and (DATE_FORMAT(A.validPeriodEnd,'%Y/%m/%d')) <= #{dateTo,jdbcType=VARCHAR}

		SELECT count(1)
		from t_goods_group A
	    inner join t_goods B
	    on A.goodsId = B.goodsId
	    inner join t_goods_price C
	    on A.goodsId = C.goodsId
		WHERE A.openFlg  <>  '9'

			and B.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')


        	and A.goodsId = #{goodsId,jdbcType=VARCHAR}


        	and A.openFlg = #{isOpenFlag,jdbcType=VARCHAR}


        	and (DATE_FORMAT(A.validPeriodStart,'%Y/%m/%d')) >= #{dateFrom,jdbcType=VARCHAR}


        	and (DATE_FORMAT(A.validPeriodEnd,'%Y/%m/%d')) <= #{dateTo,jdbcType=VARCHAR}
